using System;
using System.Collections.Generic;
using System.Text;

namespace MidiSheetMusic
{
    public class Rectangle
    {
        public int X;
        public int Y;
        public int Width;
        public int Height;

        public Rectangle(int x, int y, int width, int height)
        {
            X = x;
            Y = y;
            Width = width;
            Height = height;
        }
    }
}
